function goBackHome(){window.scrollTo({top:0,left:0,behavior:"smooth"})}let upiWrapper=document.querySelector(".upi-qr-wrapper img"),modalBody=document.querySelector(".modal-body"),preLoader=document.querySelector(".preloader"),temp="";const loader=()=>{document.body.style.overflow="hidden",setTimeout(()=>{preLoader.style.display="none",document.body.style.overflow="auto"},3e3)};window.addEventListener("DOMContentLoaded",()=>{}),upiWrapper.addEventListener("click",e=>{temp+='<img src="QR_Code.jpg" alt="payment-upi-qr-code">',""===modalBody.innerHTML||" "===modalBody.innerHTML||"\n"===modalBody.innerHTML?(upiWrapper.innerHTML="",modalBody.innerHTML=temp):(upiWrapper.style.display="block",upiWrapper.innerHTML=temp)}),window.addEventListener("scroll",e=>{let o=Math.floor(window.scrollY);document.querySelector(".back-home-icon").style.display=o>=1550?"block":"none"});

// Carousel Controls
    const carouselTrack = document.getElementById('carouselTrack');
    const pauseBtn = document.getElementById('pauseBtn');
    const pauseIcon = document.getElementById('pauseIcon');
    const pauseText = document.getElementById('pauseText');
    const speedBtn = document.getElementById('speedBtn');

    let isPaused = false;
    let currentSpeed = 30; // seconds

    // Pause/Play Toggle
    pauseBtn.addEventListener('click', function() {
        isPaused = !isPaused;
        if (isPaused) {
            carouselTrack.style.animationPlayState = 'paused';
            pauseIcon.classList.remove('fa-pause');
            pauseIcon.classList.add('fa-play');
            pauseText.textContent = 'Play';
        } else {
            carouselTrack.style.animationPlayState = 'running';
            pauseIcon.classList.remove('fa-play');
            pauseIcon.classList.add('fa-pause');
            pauseText.textContent = 'Pause';
        }
    });

    // Speed Control
    speedBtn.addEventListener('click', function() {
        if (currentSpeed === 30) {
            currentSpeed = 15;
            speedBtn.innerHTML = '<i class="fa-solid fa-gauge"></i><span>Normal Speed</span>';
        } else if (currentSpeed === 15) {
            currentSpeed = 10;
            speedBtn.innerHTML = '<i class="fa-solid fa-gauge-simple"></i><span>Slow Down</span>';
        } else {
            currentSpeed = 30;
            speedBtn.innerHTML = '<i class="fa-solid fa-gauge-high"></i><span>Speed Up</span>';
        }
        carouselTrack.style.animationDuration = currentSpeed + 's';
    });